import React, { useEffect } from "react";

const clientId = "14b03a5dcb58a775e19c";
const redirectUri = "http://localhost:3000/"; // Make sure it's EXACTLY this in Clever dashboard
const cleverAuthUrl = `https://clever.com/oauth/authorize?response_type=code&redirect_uri=${encodeURIComponent(
  redirectUri
)}&client_id=${clientId}`;

function App() {
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get("code");
    const error = urlParams.get("error");

    if (code) {
      console.log("Clever OAuth Code:", code);
    } else if (error) {
      console.error("OAuth Error:", error);
    }
  }, []);

  const handleLogin = () => {
    window.location.href = cleverAuthUrl;
  };

  return (
    <div style={{ padding: 40 }}>
      <h2>Clever Login Integration</h2>
      <button onClick={handleLogin}>Login with Clever</button>
    </div>
  );
}

export default App;
