import React, { useEffect, useState } from "react"; // Import useState

const clientId = "14b03a5dcb58a775e19c";
const redirectUri = "http://localhost:3000"; // Make sure it's EXACTLY this in Clever dashboard
const cleverAuthUrl = `https://clever.com/oauth/authorize?response_type=code&redirect_uri=${encodeURIComponent(
  redirectUri
)}&client_id=${clientId}`;

function App() {
  const [studentData, setStudentData] = useState(null); // State to store student data
  const [loading, setLoading] = useState(false); // State for loading indicator
  const [error, setError] = useState(null); // State for errors

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get("code");
    const cleverError = urlParams.get("error"); // Rename to avoid conflict with local error state

    if (code) {
      console.log("Clever OAuth Code:", code);
      // Now, send this code to your backend to exchange it for an access token and fetch user data
      // fetchStudentData(code);

      // Clean up the URL to remove the code parameter (optional, but good practice)
      const newUrl = new URL(window.location.href);
      newUrl.searchParams.delete("code");
      window.history.replaceState({}, document.title, newUrl.toString());
    } else if (cleverError) {
      console.error("OAuth Error from Clever:", cleverError);
      setError(`Clever Login Error: ${cleverError}`);
    }
  }, []); // Empty dependency array means this runs once on mount

  const fetchStudentData = async (code) => {
    setLoading(true);
    setError(null);
    try {
      // Make a request to your backend endpoint (e.g., /api/clever-callback)
      // This endpoint on your server will handle the token exchange and data fetching
      const response = await fetch(
        `http://localhost:3001/api/clever-callback?code=${code}`
      ); // Assuming your backend runs on port 3001

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(
          errorData.message || "Failed to fetch student data from backend"
        );
      }

      const data = await response.json();
      console.log("Student Data from Backend:", data);
      setStudentData(data.userData); // Assuming your backend sends data in { userData: ... }
    } catch (err) {
      console.error("Error fetching student data:", err);
      setError(`Error getting student data: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = () => {
    window.location.href = cleverAuthUrl;
  };

  return (
    <div style={{ padding: 40 }}>
      <h2>Clever Login Integration</h2>
      {!studentData && !loading && !error && (
        <button onClick={handleLogin}>Login with Clever</button>
      )}

      {loading && <p>Loading student data...</p>}

      {error && <p style={{ color: "red" }}>Error: {error}</p>}

      {studentData && (
        <div>
          <h3>
            Welcome, {studentData.name.first} {studentData.name.last}!
          </h3>
          <p>Role: {studentData.roles[0]}</p>
          <p>Clever ID: {studentData.id}</p>
          {/* You can display more student data here based on what Clever's /me endpoint returns */}
          {studentData.student_number && (
            <p>Student Number: {studentData.student_number}</p>
          )}
          {studentData.school && <p>School ID: {studentData.school}</p>}{" "}
          {/* Note: This is usually just the ID, you'd need another API call to get full school details if needed */}
          <p>
            <pre>{JSON.stringify(studentData, null, 2)}</pre>{" "}
            {/* Display raw data for debugging */}
          </p>
        </div>
      )}
    </div>
  );
}

export default App;
