import React, { useEffect, useState } from "react";

const YOUR_CLIENT_ID = "14b03a5dcb58a775e19c";
const YOUR_REDIRECT_URI = "http://localhost:3000";

// Your backend server URL.
const YOUR_BACKEND_URL = "http://localhost:5000";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [tokenData, setTokenData] = useState(null);

  // Checks if the current URL is a Clever callback
  const isCleverCallback = () => {
    const urlParams = new URLSearchParams(window.location.search);
    return urlParams.has("code");
  };

  const CleverCallback = () => {
    useEffect(() => {
      const urlParams = new URLSearchParams(window.location.search);
      const code = urlParams.get("code");

      if (code) {
        console.log("Received authorization code:", code);
        // Step 2: Send the code to your backend for token exchange
        fetch(`${YOUR_BACKEND_URL}/api/clever/token`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ code }),
        })
          .then((response) => response.json())
          .then((data) => {
            console.log(
              "Access token received from backend:",
              data.accessToken
            );
            setIsLoggedIn(true);
            setTokenData(data);
            window.history.pushState({}, "", "/");
          })
          .catch((error) => {
            console.error("Error exchanging code:", error);
          });
      }
    }, []);

    return <div>Processing Clever login...</div>;
  };

  const handleLogin = () => {
    const cleverAuthUrl = "https://clever.com/oauth/authorize";
    const params = new URLSearchParams({
      response_type: "code",
      redirect_uri: YOUR_REDIRECT_URI,
      client_id: YOUR_CLIENT_ID,
      scope: "read:students",
      state: Math.random().toString(36).substring(2, 15),
    });
    window.location.href = `${cleverAuthUrl}?${params.toString()}`;
  };

  // Conditional rendering based on whether the URL contains a callback code
  if (isCleverCallback()) {
    return <CleverCallback />;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1>Clever Login Example</h1>
      {!isLoggedIn ? (
        <button onClick={handleLogin}>Login with Clever</button>
      ) : (
        <div>
          <h2>Successfully logged in!</h2>
          <pre>{JSON.stringify(tokenData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;

